import React, { useState } from 'react';
import axios from 'axios';
import Workflow from './Workflow';

const App = () => {
  const [input, setInput] = useState('');
  const [workflow, setWorkflow] = useState(null);

  const handleSubmit = async () => {
    try {
      const response = await axios.post('http://localhost:8000/process', { user_input: input });
      setWorkflow(response.data.workflow);
      console.log('Setting workflow state:', response.data.workflow);
      setWorkflow(response.data.workflow);
      console.log('Workflow state updated:', workflow);  // Workflow state should now have content.

    } catch (error) {
      console.error('Error generating workflow:', error);
    }
  };

  return (
    <div className="app-container">
      {!workflow ? (
        <div className="input-container">
          <input
            type="text"
            placeholder="Enter your text..."
            value={input}
            onChange={(e) => setInput(e.target.value)}
          />
          <button onClick={handleSubmit}>Submit</button>
        </div>
      ) : (
        <Workflow workflow={workflow} />
      )}
    </div>
  );
};

export default App;
