import React, { useState } from 'react';

const Workflow = ({ workflow }) => {
  const groupedByPhase = workflow.reduce((acc, subtask) => {
    acc[subtask.phase] = acc[subtask.phase] || [];
    acc[subtask.phase].push(subtask);
    return acc;
  }, {});

  const [selectedStep, setSelectedStep] = useState(null);
  const [expandedSteps, setExpandedSteps] = useState({});

  const handleStepClick = (subtask) => {
    setSelectedStep((prev) => (prev === subtask ? null : subtask));
  };

  const toggleStepDetails = (stepIndex) => {
    setExpandedSteps((prev) => ({
      ...prev,
      [stepIndex]: !prev[stepIndex], // Toggle step open/closed state
    }));
  };

  return (
    <div className="workflow-container">
      {Object.entries(groupedByPhase).map(([phase, subtasks]) => (
        <div key={phase} className="phase-row">
          <div className="phase-hover-area"></div>
          <div className="phase-label-container">
            <span className="phase-label-text">Phase: {phase}</span>
          </div>
          <div className="subtasks-wrapper">
            {subtasks.map((subtask, index) => (
              <div
                key={index}
                className="subtask"
                onClick={() => handleStepClick(subtask)}
              >
                <h2 className="subtask-title">{subtask.name}</h2>
                <p className="subtask-description">{subtask.description}</p>

                {selectedStep === subtask && (
                  <div className="steps">
                    {subtask.steps.map((step, i) => (
                      <div key={i} className="step">
                        <h3
                          className="step-name"
                          onClick={() => toggleStepDetails(i)}
                        >
                          {step.name}
                        </h3>

                        {expandedSteps[i] && (
                          <div className="step-details">
                            <div className="step-attribute">
                              <strong>Description:</strong> {step.description}
                            </div>
                            <div className="step-attribute">
                              <strong>Status:</strong> {step.rationale}
                            </div>
                            <div className="step-attribute">
                              <strong>Due Date:</strong> {step.role}
                            </div>
                          </div>
                        )}
                      </div>
                    ))}
                  </div>
                )}
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
};

export default Workflow;
