import React, { useState } from "react";
import { Link } from "react-router-dom";
import "../styles/Sidebar.css";

const Sidebar = () => {
  const [isOpen, setIsOpen] = useState(true);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [profile, setProfile] = useState(null);

  const toggleSidebar = () => {
    setIsOpen(!isOpen);
  };

  const handleLoginSuccess = (response) => {
    console.log("Google login successful:", response);
    setProfile(response.profileObj);
    setIsLoggedIn(true);
  };

  const handleLoginFailure = (response) => {
    console.error("Google login failed:", response);
  };

  return (
    <div className={`sidebar ${isOpen ? "open" : "collapsed"}`}>
      <button className="toggle-button" onClick={toggleSidebar}>
        <img src="/icons/collapse.svg" alt="Collapse Icon" className="icon" />
      </button>
      {isOpen && (
        <>
          <h1 className="website-title">haX</h1>
          <ul>
            <li>
              <Link to="/">
                <img src="/icons/home.svg" alt="Home Icon" className="icon" />{" "}
                Home
              </Link>
            </li>
            <li>
              <Link to="/community">
                <img
                  src="/icons/global.svg"
                  alt="Community Icon"
                  className="icon"
                />{" "}
                Community
              </Link>
            </li>
            <li>
              <Link to="/saved">
                <img src="/icons/save.svg" alt="Saved Icon" className="icon" />{" "}
                Saved
              </Link>
            </li>
            <li>
              <Link to="/reward">
                <img src="/icons/coin.svg" alt="Reward Icon" className="icon" />{" "}
                Reward
              </Link>
            </li>
          </ul>
          <div className="profile-section">
            {!isLoggedIn ? (
              // Render the GoogleLogin button with a custom render to display our account icon
              <GoogleLogin
                clientId="YOUR_GOOGLE_CLIENT_ID" // Replace with your actual Google client ID
                buttonText="Login with Google"
                onSuccess={handleLoginSuccess}
                onFailure={handleLoginFailure}
                cookiePolicy={"single_host_origin"}
                render={(renderProps) => (
                  <button
                    onClick={renderProps.onClick}
                    disabled={renderProps.disabled}
                    className="account-button"
                    style={{
                      background: "none",
                      border: "none",
                      cursor: "pointer",
                    }}
                  >
                    <img
                      src="/icons/ellipse.svg"
                      alt="Account Icon"
                      className="account-icon"
                    />
                  </button>
                )}
              />
            ) : (
              <div className="profile-details">
                <img
                  src={profile.imageUrl}
                  alt="Profile"
                  className="account-icon"
                />
                <div className="descriptions">
                  <h3 className="username">{profile.name}</h3>
                  <p className="email">{profile.email}</p>
                </div>
              </div>
            )}
          </div>
        </>
      )}
    </div>
  );
};

export default Sidebar;
