import React, { useState } from 'react';
import axios from 'axios';
import Workflow from './Workflow';

const App = () => {
  const [input, setInput] = useState('');
  const [workflow, setWorkflow] = useState(null);
  const [loading, setLoading] = useState(false); // New state to track loading

  const handleSubmit = async () => {
    try {
      const response = await axios.post('http://localhost:8000/process', {user_input: input});

      // Convert the response into an array of subtasks with parsed steps
      const parsedWorkflow = Object.entries(response.data).map(([subtaskName, details]) => ({
        name: subtaskName,
        description: details.description,
        steps: details.workflow.split('\n')  // Split the string into steps
      }));

      setWorkflow(parsedWorkflow);
    } catch (error) {
      console.error('Error generating workflow:', error);
    }
  };

  return (
    <div className="app-container">
      <h1 className="website-title">DO.AI</h1> {/* Website Title */}
      {!workflow ? (
        <div className="input-container">
          <input
            type="text"
            placeholder="Enter your text..."
            value={input}
            onChange={(e) => setInput(e.target.value)}
          />
          <button onClick={handleSubmit} disabled={loading}>
            {loading ? ( 
              <i className="fas fa-spinner fa-spin"></i> // Spinner while loading
            ) : (
              <i className="fas fa-paper-plane"></i> // Paper plane icon
            )}
          </button>
        </div>
      ) : (
        <Workflow workflow={workflow} />
      )}
    </div>
  );
};

export default App;
