import React, { useState, useRef } from 'react';
import { TransformWrapper, TransformComponent } from 'react-zoom-pan-pinch';
import '../styles/Workflow.css';

const Workflow = ({ workflow, phaseDict }) => {
  const transformRef = useRef(); // Create a ref to access TransformWrapper's methods

  const groupedByPhase = workflow.reduce((acc, subtask) => {
    acc[subtask.phase] = acc[subtask.phase] || [];
    acc[subtask.phase].push(subtask);
    return acc;
  }, {});

  // Zoom controls using TransformWrapper's instance methods
  const zoomIn = () => transformRef.current.zoomIn();
  const zoomOut = () => transformRef.current.zoomOut();
  const resetZoom = () => transformRef.current.resetTransform();

  return (
    <div className="workflow-page">
      {/* Zoom Controls */}
      <div className="zoom-controls">
        <button onClick={zoomOut}>-</button>
        <button onClick={zoomIn}>+</button>
        <button onClick={resetZoom}>Reset</button>
      </div>

      {/* Zoomable Workflow Canvas */}
      <TransformWrapper ref={transformRef} initialScale={1} minScale={0.5} maxScale={2}>
        <TransformComponent>
          <div className="workflow-container">
            {Object.entries(groupedByPhase).map(([phase, subtasks], phaseIndex) => (
              <div key={phase} className="phase-row">
                <div className="phase-label-container">
                  <span className="phase-label-text">
                    {`PHASE ${phaseIndex + 1} - ${phaseDict[phase] || `Phase ${phase}`}`}
                  </span>
                </div>
                <div className="subtasks-wrapper">
                  {subtasks.map((subtask, subtaskIndex) => (
                    <div key={subtaskIndex} className="subtask">
                      <h2 className="subtask-title">{subtask.description}</h2>
                      <div className="steps">
                        {subtask.steps.map((step, stepIndex) => (
                          <div key={stepIndex} className="step">
                            <div className="step-header">
                              <div className="step-indexCircle">{stepIndex + 1}</div>
                              <div className="step-name">{step.name}</div>
                            </div>
                            <div className="step-details">
                              <strong>Objective:</strong> {step.objective}
                            </div>
                          </div>
                        ))}
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            ))}
          </div>
        </TransformComponent>
      </TransformWrapper>
    </div>
  );
};

export default Workflow;
