import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes} from 'react-router-dom';
import Sidebar from './Sidebar';
import Dashboard from './Dashboard';
import ProjectPage from './ProjectPage';
import '../styles/App.css'; // Import the CSS

const App = () => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  return (
    <Router>
      <div className="app-container">
        {/* Use your custom Sidebar component */}
        <div className={`sidebar-container ${isSidebarOpen ? 'open' : 'collapsed'}`}>
          <Sidebar /> 
          <button className="toggle-btn" onClick={toggleSidebar}>
            {isSidebarOpen ? '<' : '>'}
          </button>
        </div>
        <div className={`content-area ${isSidebarOpen ? 'shifted' : ''}`}>
          <Routes>
            <Route path="/start-project" element={<ProjectPage />} />
            <Route path="/" element={<Dashboard />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
};

export default App;
