import React, { useState } from 'react';
import '../styles/Workflow.css';

const Workflow = ({ workflow }) => {
  const [selectedStep, setSelectedStep] = useState(null); // Track selected step

  const handleStepClick = (subtask) => {
    if (selectedStep === subtask) {
      // If the same subtask is clicked, close it (toggle off)
      setSelectedStep(null);
    } else {
      // Otherwise, open the selected subtask
      setSelectedStep(subtask);
    }
  };

  return (
    <div className="workflow-container">
      {workflow.map((subtask, index) => (
        <div key={index} className="subtask">
          <h2 
            onClick={() => handleStepClick(subtask)} 
            className="subtask-title"
          >
            {subtask.name}
          </h2>
          <p className="subtask-description">{subtask.description}</p>

          {selectedStep === subtask && (
            <div className="steps">
              {subtask.steps.map((step, i) => (
                <div key={i} className="step" onClick={() => alert(step)}>
                  {step}
                </div>
              ))}
            </div>
          )}
        </div>
      ))}
    </div>
  );
};

export default Workflow;
